function loadMore(range, type) {
    const docElement = type == "mobile" ? mobileIndexes : indexesContainer;

    const limit = type == "mobile" ? (mobileCount + range) : (mainCount + range);


    for (let index = type == "mobile" ? mobileCount : mainCount; index < teluguSongData.length && index < limit; index++) {
        const element = teluguSongData[index];
        let title = element.teluguTitle;
        if (title.length > 25) {
            title = title.substring(0, 25) + "...";
        }
        let spanStr = `<a href="" class="items-center"><li class=" cursor-pointer pt-3 pb-2 pl-3 mb-2 rounded-md text-black text-lg hover:bg-green-600  hover:text-white"><span class="pl-5"> ${title}</span></li></a>`
        docElement.innerHTML = docElement.innerHTML + spanStr;
        if (type == "mobile") {
            mobileCount++;
        } else {
            mainCount++;
        }
    }
}

function floatLoadMore(range) {
    loadMore(range, "mobile");
}


function mainLoadMore(range) {
    loadMore(range, "largerscreen");
}



