function sortTitles(query) {
    let containsList = teluguSongData.filter(title => title.teluguTitle.startsWith(query));
    searchResultsUL.innerHTML = "";
    if (containsList.length != 0) {
        containsList.forEach(element => {
            let html = `  <a href="" class=""><li class=" cursor-pointer pt-3 pb-2 pl-3 mb-2 rounded-md text-black text-lg hover:bg-green-600  hover:text-white"><span class="pl-5"> ${element.teluguTitle}</span></li></a>`;
            searchResultsUL.innerHTML = searchResultsUL.innerHTML + html;
        });
    } else {
        searchResultsUL.innerHTML = `<h2>Nothing Found ☹ </h2>`
    }

}



